import React, { Component } from "react";
import { ColumnProps, ColumnState, UserDataAPI } from "./interface";
import { Item } from "./Item";
import axios from "axios";

export class Column extends React.Component<ColumnProps, ColumnState> {
  constructor(props: ColumnProps) {
    super(props);

    this.state = {
      items: [],
    };

    console.log("constructor");
  }

  handleAddClick = () => {
    console.log("AddClick");

    console.log("Fetching Data...");
    axios
      .get(`https://reqres.in/api/users/${this.state.items.length + 1}`)
      .then((response) => {
        const userDataAPI = response.data as UserDataAPI;

        this.setState((state) => {
          console.log("Before Items: ", state.items);
          const items = state.items.concat({
            name: userDataAPI.data.first_name,
          });
          console.log("After Items: ", items);
          return {
            items,
          };
        });
      });
  };

  handleMinusClick = () => {
    console.log("MinusClick");
    this.setState((state) => {
      const [head, ...tail] = state.items;

      return {
        items: tail,
      };
    });
  };

  render() {
    console.log("Rendering...");
    const { title } = this.props;
    const listItems = this.state.items.map((item) => (
      <li key={item.name}>{item.name}</li>
    ));

    return (
      <div>
        {title}
        <ul>
          {this.state.items.map((item) => (
            <Item name={item.name} />
          ))}
        </ul>
        <button onClick={this.handleAddClick}>Add</button>
        <button onClick={this.handleMinusClick}>Minus</button>
      </div>
    );
  }
}
